import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

function Home() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const navigate = useNavigate(); // For redirection after checking auth

  useEffect(() => {
    const checkAuth = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users/check-auth', {
          headers: {
            Authorization: `Bearer ${JSON.parse(localStorage.getItem('token'))}`, // Assuming you're storing the token in localStorage
          }
        });
        
        if (response.data.ok) {
          setIsAuthenticated(true);
          setUser(response.data.user);
        }
      } catch (error) {
        setIsAuthenticated(false);
        setUser(null);
        
      }
    };

    checkAuth();
  }, [navigate]); // Adding navigate as a dependency to avoid stale closures

  return (
    <div>
      
        <div className='Home'>
            <div className='yourBest' style={{backgroundImage : "linear-gradient( rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.3) ),url('https://myrhline.com/wp-content/uploads/2022/06/e-learning-session-de-formation-en-ligne.jpg.webp')"}}>
                <h1>DemaghLab: Where Curiosity Drives Discovery</h1>
                <h2>Explore Our Library Of Courses</h2>
            </div>
            <div className='coursesHolder'>
              <h1>Our Library Of Courses</h1>
              <div className='coursesWrapper'>
                <div className='courseCard'>
                  <img src='data:image/jpeg;base64,'></img>
                  <label>Python Course</label>
                  <p>Learn Python Syntax, Variables, Loops, Conditions and Functions</p>
                  <Link to={"/courses"}>Start Now</Link>
                </div>
                <div className='courseCard'>
                  <img src='data:image/png;base64,'></img>
                  <label>Node Js Course</label>
                  <p>Learn the basics of Node js and Backend Development</p>
                  <Link to={"/courses"}>Start Now</Link>
                </div>
                <div className='courseCard'>
                  <img src='data:image/png;base64,'></img>
                  <label>Machine Learning Course</label>
                  <p>Explore all the types of Machine learning Algorithms</p>
                  <Link to={"/courses"}>Start Now</Link>
                </div>
              </div>
            </div>
        </div>
      
    </div>
  );
}

export default Home;
